import { Component, OnInit } from '@angular/core';
import { WeatherData } from './models/weather.model';
import { WeatherService } from './service/weather.service';
import '../app/lit-elements/weather'

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit {
  constructor(private weatherService: WeatherService) {}
  public cityName?: string;
  public temperatureValue = 1;
  public temperatureResult?: WeatherData;

  ngOnInit(): void {
    this.weatherService.getWeatherData('Wellington').subscribe((data) => {
      this.temperatureResult = data;
      console.log('data check :', data);
    });
  }

  onSubmit() {}
}
<div class="container">
  <div class="weather-container">
    <div class="search">
      <form #form="ngForm" (ngSubmit)="onSubmit()" [(ngModel)]="cityName">
        <input type="text" placeholder="Search City">
      </form>
      <div class="upper-data">
        <img src="../assets/mountains.webp" *ngIf="temperatureValue >= 14" alt="Mountains Image">
        <img src="../assets/cold.jpg" *ngIf="temperatureValue < 14" alt="Cold Image">
      </div>

      <hr>

      <div class="weather-data">
        <div class="location">
          {{ temperatureResult?.name }}
        </div>
        <div *ngIf="temperatureValue >= 14" class="temperature">
          {{ temperatureResult?.main?.temp_max }}°C
        </div>
        <div *ngIf="temperatureValue < 14" class="temperature">
          -{{ temperatureResult?.main?.temp_min }}°C
        </div>
      </div>
    </div>
  </div>
</div>
<weather-display></weather-display>
